  //
  topicEnginePrepare() {
    $.topics = {};
    $.Topic = function (id) {
      //check if id is provided and if it is new id
      let callbacks,
        topic = id && $.topics[id];

      if (!topic) {
        callbacks = $.Callbacks();
        topic = {
          publish: callbacks.fire,
          publishWith: callbacks.fireWith,
          subscribe: callbacks.add,
          unsubscribe: callbacks.remove,
        };
        if (id) {
          $.topics[id] = topic;
        }
      }
      return topic;
    };
  }

